import React from "react";

import PopupWithForm from "./PopupWithForm";

function EditProfilePopup({ isOpen, isClose }) {

  const [name, setName] = React.useState("");
  const [description, setDescription] = React.useState("");

  function handleChange(evt) {
    setName(evt.target.value);
  }

  return (
    <PopupWithForm
      name="edit-profile"
      title="Редактировать профиль"
      isOpen={isOpen}
      onClose={isClose}
      buttonSubmitText="Сохранить"
    >
      <>
        <input className="popup__input"
          id="profile-name"
          name="name"
          minLength="2"
          maxLength="40"
          type="text"
          placeholder="Имя"
          required
          autoComplete="off"
          value={name}
          onChange={handleChange}
        />
        <span className="popup__error profile-name-error">
        </span>
        <input className="popup__input" id="profile-about" name="about" minLength="2" maxLength="200" type="text"
          placeholder="О себе" required autoComplete="off" />
        <span className="popup__error profile-about-error">
        </span>
      </>
    </PopupWithForm>
  )

}

export default EditProfilePopup;
