function Main() {

  function handleEditAvatarClick() {
    const popupEditAvatar = document.querySelector('.popup_type_edit-avatar');
    popupEditAvatar.classList.add('popup_opened');
  }

  function handleEditProfileClick() {
    const popupEditProfile = document.querySelector('.popup_type_edit-profile');
    popupEditProfile.classList.add('popup_opened');
  }

  function handleAddPlaceClick() {
    const popupAddPlace = document.querySelector('.popup_type_add-card');
    popupAddPlace.classList.add('popup_opened');
  }

  return (
    <main class="content">
      <section class="profile">
        <div class="profile__avatar-container">
          <img class="profile__avatar" src="#" alt="Аватарка" />
          <button class="profile__avatar-button"
                  type="button"
                  onClick={handleEditAvatarClick}>
          </button>
        </div>
        <div class="profile__info">
          <h1 class="profile__name">
            Жак-Ив Кусто
          </h1>
          <button class="profile__redaction" type="button">
          </button>
          <p class="profile__about">
            Исследователь океана
          </p>
        </div>
        <button class="profile__add-button" type="button">
        </button>
      </section>

      <section class="cards">
      </section>
    </main>
  );
}

export default Main;
