import React, { Children } from "react";

import { Link } from "react-router-dom";

function Register({ auth }) {

  const [newUserData, setNewUserData] = React.useState({});

  function handleDataChange(evt) {
    const { name, value } = evt.target;
    setNewUserData({
      [name]: value
    });
    console.log(newUserData);
  }

  function handleDataSubmit(evt) {
    evt.preventDefault();
    auth.registration(newUserData.email, newUserData.password)
      .then((res) => {
        if (res) {
          Children.history.push('/login');
        }
      })
  }

  return (
    <>
      <form
        className="sign__container"
        name="login-form"
        onClick={handleDataSubmit}
        >
        <h3 className="sign__title">
          Регистрация
        </h3>

        <input
          className="sign__input"
          id="login"
          name="login"
          type="email"
          placeholder="Email"
          required
          autoComplete="off"
          onChange={handleDataChange}
        />

        <input
          className="sign__input"
          id="password"
          name="password"
          type="password"
          placeholder="Пароль"
          required
          autoComplete="off"
          onChange={handleDataChange}
        />

        <button
          className="sign__button-submit"
          type="submit">
          Зарегистрироваться
        </button>
      </form>
      <p
        className="sign__caption">
        Уже зарегистрированы?
        <Link
          to="/sign-in"
          className="sign__link"
        > Войти</Link>
      </p>
    </>
  )
}


export default Register;
